import React, { useState, useEffect } from 'react';
import {
  Container,
  Card,
  Button,
  Modal,
  Form,
  Alert,
  Badge,
  Table,
  Spinner,
} from 'react-bootstrap';
import { membershipAPI, userAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const MembershipManagement = () => {
  const [memberships, setMemberships] = useState([]);
  const [users, setUsers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingMembership, setEditingMembership] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { isAuthenticated, user } = useAuth(); // Get logged-in user info
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    userId: '',
    type: 'BASIC',
    price: '',
    startDate: '',
    endDate: '',
    description: '',
  });

  // Role-based access control helper
  const canManageUser = (managedUserRole) => {
    if (user.role === 'ADMIN') return true;
    if (user.role === 'STAFF') {
      return ['STAFF', 'TRAINER', 'MEMBER'].includes(managedUserRole);
    }
    return false;
  };

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    // Redirect if unauthorized role
    if (!['ADMIN', 'STAFF'].includes(user.role)) {
      navigate('/unauthorized'); // redirect or show unauthorized page
      return;
    }

    fetchMemberships();
    fetchUsers();
  }, [isAuthenticated, navigate, user.role]);

  const fetchMemberships = async () => {
    try {
      const response = await membershipAPI.getAll();
      setMemberships(response.data);
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Authentication required. Please login again.');
        navigate('/login');
      } else {
        setError('Failed to fetch memberships');
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await userAPI.getAll();
      setUsers(response.data);
    } catch {
      console.error('Failed to fetch users');
    }
  };

  // Filter memberships and users based on role
  const filteredMemberships = memberships.filter((m) =>
    canManageUser(m.user.role)
  );
  const filteredUsers = users.filter((u) => canManageUser(u.role));

  const resetForm = () => {
    setFormData({
      userId: '',
      type: 'BASIC',
      price: '',
      startDate: '',
      endDate: '',
      description: '',
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const selectedUser = users.find((u) => u.id === Number(formData.userId));
    if (!selectedUser || !canManageUser(selectedUser.role)) {
      setError('You do not have permission to manage membership for this user');
      return;
    }

    if (new Date(formData.startDate) > new Date(formData.endDate)) {
      setError('Start date cannot be after end date');
      return;
    }
    if (formData.price <= 0) {
      setError('Price must be greater than 0');
      return;
    }

    const payload = {
      userId: Number(formData.userId),
      type: formData.type,
      price: parseFloat(formData.price),
      startDate: `${formData.startDate}T00:00:00`,
      endDate: `${formData.endDate}T00:00:00`,
      description: formData.description,
    };

    try {
      setSubmitting(true);
      if (editingMembership) {
        // Extra permission check on editing membership user
        const editingUser = users.find(
          (u) => u.id === editingMembership.user.id
        );
        if (!editingUser || !canManageUser(editingUser.role)) {
          setError('You do not have permission to edit this membership');
          setSubmitting(false);
          return;
        }
        await membershipAPI.update(editingMembership.id, payload);
        setSuccess('Membership updated successfully');
      } else {
        await membershipAPI.create(payload);
        setSuccess('Membership created successfully');
      }
      setShowModal(false);
      setEditingMembership(null);
      resetForm();
      fetchMemberships();
    } catch (err) {
      setError(err.response?.data?.message || 'Operation failed');
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (membership) => {
    if (!canManageUser(membership.user.role)) {
      setError('You do not have permission to edit this membership');
      return;
    }
    setEditingMembership(membership);
    setFormData({
      userId: membership.user.id,
      type: membership.type,
      price: membership.price,
      startDate: membership.startDate.split('T')[0],
      endDate: membership.endDate.split('T')[0],
      description: membership.description || '',
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    const membership = memberships.find((m) => m.id === id);
    if (!membership || !canManageUser(membership.user.role)) {
      setError('You do not have permission to delete this membership');
      return;
    }
    if (window.confirm('Are you sure you want to delete this membership?')) {
      try {
        await membershipAPI.delete(id);
        setSuccess('Membership deleted successfully');
        fetchMemberships();
      } catch {
        setError('Failed to delete membership');
      }
    }
  };

  const updateStatus = async (id, newStatus) => {
    const membership = memberships.find((m) => m.id === id);
    if (!membership || !canManageUser(membership.user.role)) {
      setError('You do not have permission to update this membership');
      return;
    }
    try {
      await membershipAPI.updateStatus(id, newStatus);
      setSuccess(`Status updated to ${newStatus}`);
      fetchMemberships();
    } catch {
      setError('Failed to update status');
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      ACTIVE: 'success',
      EXPIRED: 'danger',
      CANCELLED: 'warning',
      PENDING: 'info',
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const getTypeBadge = (type) => {
    const variants = {
      BASIC: 'primary',
      PREMIUM: 'warning',
      VIP: 'danger',
      STUDENT: 'info',
    };
    return <Badge bg={variants[type] || 'secondary'}>{type}</Badge>;
  };

  if (loading) {
    return (
      <div className="text-center mt-4">
        <Spinner animation="border" /> Loading memberships...
      </div>
    );
  }

  return (
    <Container className="mt-4">
      <Card>
        <Card.Header className="d-flex justify-content-between align-items-center">
          <h4>Membership Management</h4>
          <Button variant="primary" onClick={() => setShowModal(true)}>
            Add New Membership
          </Button>
        </Card.Header>
        <Card.Body>
          {error && (
            <Alert
              variant="danger"
              dismissible
              onClose={() => setError('')}
            >
              {error}
            </Alert>
          )}
          {success && (
            <Alert
              variant="success"
              dismissible
              onClose={() => setSuccess('')}
            >
              {success}
            </Alert>
          )}

          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Member</th>
                <th>Type</th>
                <th>Status</th>
                <th>Price</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredMemberships.map((m) => (
                <tr key={m.id}>
                  <td>
                    {m.user.firstName} {m.user.lastName}
                  </td>
                  <td>{getTypeBadge(m.type)}</td>
                  <td>{getStatusBadge(m.status)}</td>
                  <td>${m.price}</td>
                  <td>{new Date(m.startDate).toLocaleDateString()}</td>
                  <td>{new Date(m.endDate).toLocaleDateString()}</td>
                  <td>
                    <Button
                      size="sm"
                      variant="outline-primary"
                      onClick={() => handleEdit(m)}
                    >
                      Edit
                    </Button>{' '}
                    <Button
                      size="sm"
                      variant="outline-danger"
                      onClick={() => handleDelete(m.id)}
                    >
                      Delete
                    </Button>{' '}
                    <Button
                      size="sm"
                      variant="outline-secondary"
                      onClick={() => updateStatus(m.id, 'CANCELLED')}
                    >
                      Cancel
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>

      <Modal
        show={showModal}
        onHide={() => {
          setShowModal(false);
          setEditingMembership(null);
          resetForm();
          setError('');
        }}
      >
        <Modal.Header closeButton>
          <Modal.Title>
            {editingMembership ? 'Edit Membership' : 'Add New Membership'}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>Member</Form.Label>
              <Form.Select
                value={formData.userId}
                onChange={(e) =>
                  setFormData({ ...formData, userId: e.target.value })
                }
                required
                disabled={submitting}
              >
                <option value="">Select Member</option>
                {/* Render options based on role */}
                {user.role === 'ADMIN' &&
                  filteredUsers.map((u) => (
                    <option key={u.id} value={u.id}>
                      {u.firstName} {u.lastName} ({u.username} - {u.role})
                    </option>
                  ))}

                {user.role === 'STAFF' &&
                  filteredUsers
                    .filter((u) =>
                      ['STAFF', 'TRAINER', 'MEMBER'].includes(u.role)
                    )
                    .map((u) => (
                      <option key={u.id} value={u.id}>
                        {u.firstName} {u.lastName} ({u.username} - {u.role})
                      </option>
                    ))}
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Type</Form.Label>
              <Form.Select
                value={formData.type}
                onChange={(e) =>
                  setFormData({ ...formData, type: e.target.value })
                }
                required
                disabled={submitting}
              >
                <option value="BASIC">BASIC</option>
                <option value="PREMIUM">PREMIUM</option>
                <option value="VIP">VIP</option>
                <option value="STUDENT">STUDENT</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                min="0.01"
                step="0.01"
                value={formData.price}
                onChange={(e) =>
                  setFormData({ ...formData, price: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type="date"
                value={formData.startDate}
                onChange={(e) =>
                  setFormData({ ...formData, startDate: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type="date"
                value={formData.endDate}
                onChange={(e) =>
                  setFormData({ ...formData, endDate: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description (optional)</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={formData.description}
                onChange={(e) =>
                  setFormData({ ...formData, description: e.target.value })
                }
                disabled={submitting}
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => {
                setShowModal(false);
                setEditingMembership(null);
                resetForm();
                setError('');
              }}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button type="submit" variant="primary" disabled={submitting}>
              {submitting ? 'Saving...' : 'Save'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  );
};

export default MembershipManagement;
