import { membershipAPI, authAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const MembershipManagement = () => {
  const [memberships, setMemberships] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const { isAuthenticated, user } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    // User info
    firstName: '',
    lastName: '',
    username: '',
    email: '',
    password: '',
    role: 'MEMBER',

    // Membership info
    type: 'BASIC',
    price: '',
    startDate: '',
    endDate: '',
    description: '',
  });

  // Allowed roles for registration based on current user role
  const allowedRoles = () => {
    if (user.role === 'ADMIN') return ['ADMIN', 'STAFF', 'TRAINER', 'MEMBER'];
    if (user.role === 'STAFF') return ['STAFF', 'TRAINER', 'MEMBER'];
    return [];
  };

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    if (!['ADMIN', 'STAFF'].includes(user.role)) {
      navigate('/unauthorized');
      return;
    }
    fetchMemberships();
  }, [isAuthenticated, user.role, navigate]);

  // Fetch all memberships
  const fetchMemberships = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await membershipAPI.getAll();
      setMemberships(res.data);
    } catch {
      setError('Failed to load memberships.');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      firstName: '',
      lastName: '',
      username: '',
      email: '',
      password: '',
      role: 'MEMBER',
      type: 'BASIC',
      price: '',
      startDate: '',
      endDate: '',
      description: '',
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validate role permission
    if (!allowedRoles().includes(formData.role)) {
      setError(`You cannot register a user with role: ${formData.role}`);
      return;
    }

    // Validate dates
    if (new Date(formData.startDate) > new Date(formData.endDate)) {
      setError('Start date cannot be after end date.');
      return;
    }

    if (Number(formData.price) <= 0) {
      setError('Price must be greater than 0.');
      return;
    }

    try {
      setSubmitting(true);

      // 1. Register user
      const userPayload = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        username: formData.username,
        email: formData.email,
        password: formData.password,
        role: [formData.role], // API expects an array of roles
      };
      const userRes = await authAPI.register(userPayload);
      const newUser = userRes.data;

      // 2. Create membership for the new user
      const membershipPayload = {
        userId: newUser.id,
        type: formData.type,
        price: Number(formData.price),
        startDate: `${formData.startDate}T00:00:00`,
        endDate: `${formData.endDate}T00:00:00`,
        description: formData.description,
      };
      await membershipAPI.create(membershipPayload);

      setSuccess('User and membership created successfully.');
      setShowModal(false);
      resetForm();
      fetchMemberships();
    } catch (err) {
      setError(
        err.response?.data?.message ||
          'Failed to create user or membership.'
      );
    } finally {
      setSubmitting(false);
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      ACTIVE: 'success',
      EXPIRED: 'danger',
      CANCELLED: 'warning',
      PENDING: 'info',
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const getTypeBadge = (type) => {
    const variants = {
      BASIC: 'primary',
      PREMIUM: 'warning',
      VIP: 'danger',
      STUDENT: 'info',
    };
    return <Badge bg={variants[type] || 'secondary'}>{type}</Badge>;
  };

  if (loading) {
    return (
      <div className="text-center mt-5">
        <Spinner animation="border" /> Loading memberships...
      </div>
    );
  }

  return (
    <Container className="mt-4">
      <Card>
        <Card.Header className="d-flex justify-content-between align-items-center">
          <h4>Membership Management</h4>
          <Button
            variant="primary"
            onClick={() => {
              resetForm();
              setError('');
              setSuccess('');
              setShowModal(true);
            }}
          >
            Register New User & Membership
          </Button>
        </Card.Header>
        <Card.Body>
          {error && (
            <Alert
              variant="danger"
              dismissible
              onClose={() => setError('')}
            >
              {error}
            </Alert>
          )}
          {success && (
            <Alert
              variant="success"
              dismissible
              onClose={() => setSuccess('')}
            >
              {success}
            </Alert>
          )}

          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Member ID</th>
                <th>Membership Type</th>
                <th>Status</th>
                <th>Price</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {memberships.map((membership) => (
                <tr key={membership.id}>
                  <td>{membership.userId}</td>
                  <td>{getTypeBadge(membership.type)}</td>
                  <td>{getStatusBadge(membership.status)}</td>
                  <td>${membership.price.toFixed(2)}</td>
                  <td>{new Date(membership.startDate).toLocaleDateString()}</td>
                  <td>{new Date(membership.endDate).toLocaleDateString()}</td>
                  <td>{membership.description || '-'}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>

      <Modal
        show={showModal}
        onHide={() => {
          setShowModal(false);
          resetForm();
          setError('');
          setSuccess('');
        }}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Register New User & Membership</Modal.Title>
        </Modal.Header>

        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {/* User Fields */}
            <Form.Group className="mb-3" controlId="firstName">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                value={formData.firstName}
                onChange={(e) =>
                  setFormData({ ...formData, firstName: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="lastName">
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                value={formData.lastName}
                onChange={(e) =>
                  setFormData({ ...formData, lastName: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="username">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                value={formData.username}
                onChange={(e) =>
                  setFormData({ ...formData, username: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={formData.email}
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={formData.password}
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="role">
              <Form.Label>Role</Form.Label>
              <Form.Select
                value={formData.role}
                onChange={(e) =>
                  setFormData({ ...formData, role: e.target.value })
                }
                required
                disabled={submitting}
              >
                {allowedRoles().map((r) => (
                  <option key={r} value={r}>
                    {r}
                  </option>
                ))}
              </Form.Select>
            </Form.Group>

            {/* Membership Fields */}
            <Form.Group className="mb-3" controlId="type">
              <Form.Label>Membership Type</Form.Label>
              <Form.Select
                value={formData.type}
                onChange={(e) =>
                  setFormData({ ...formData, type: e.target.value })
                }
                required
                disabled={submitting}
              >
                <option value="BASIC">BASIC</option>
                <option value="PREMIUM">PREMIUM</option>
                <option value="VIP">VIP</option>
                <option value="STUDENT">STUDENT</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3" controlId="price">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                step="0.01"
                min="0"
                value={formData.price}
                onChange={(e) =>
                  setFormData({ ...formData, price: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="startDate">
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type="date"
                value={formData.startDate}
                onChange={(e) =>
                  setFormData({ ...formData, startDate: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="endDate">
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type="date"
                value={formData.endDate}
                onChange={(e) =>
                  setFormData({ ...formData, endDate: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="description">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={formData.description}
                onChange={(e) =>
                  setFormData({ ...formData, description: e.target.value })
                }
                disabled={submitting}
              />
            </Form.Group>
          </Modal.Body>

          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => {
                setShowModal(false);
                resetForm();
                setError('');
                setSuccess('');
              }}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button type="submit" variant="primary" disabled={submitting}>
              {submitting ? 'Submitting...' : 'Submit'}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  );
};

export default MembershipManagement;
