import React, { useState, useEffect } from 'react';
import {
  Container,
  Card,
  Button,
  Modal,
  Form,
  Alert,
  Badge,
  Table,
  Spinner,
} from 'react-bootstrap';
import { membershipAPI, userAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const MembershipManagement = () => {
  const [memberships, setMemberships] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingMembership, setEditingMembership] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { isAuthenticated, user } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    // New user fields
    firstName: '',
    lastName: '',
    username: '',
    email: '',
    role: user.role === 'ADMIN' ? 'MEMBER' : 'MEMBER',

    // Membership fields
    type: 'BASIC',
    price: '',
    startDate: '',
    endDate: '',
    description: '',
  });

  // Role-based access control helper
  const canManageUser = (managedUserRole) => {
    if (user.role === 'ADMIN') return true;
    if (user.role === 'STAFF') {
      return ['STAFF', 'TRAINER', 'MEMBER'].includes(managedUserRole);
    }
    return false;
  };

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (!['ADMIN', 'STAFF'].includes(user.role)) {
      navigate('/unauthorized');
      return;
    }

    fetchMemberships();
  }, [isAuthenticated, navigate, user.role]);

  const fetchMemberships = async () => {
    try {
      const response = await membershipAPI.getAll();
      setMemberships(response.data);
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Authentication required. Please login again.');
        navigate('/login');
      } else {
        setError('Failed to fetch memberships');
      }
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      firstName: '',
      lastName: '',
      username: '',
      email: '',
      role: user.role === 'ADMIN' ? 'MEMBER' : 'MEMBER',
      type: 'BASIC',
      price: '',
      startDate: '',
      endDate: '',
      description: '',
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validate dates & price
    if (new Date(formData.startDate) > new Date(formData.endDate)) {
      setError('Start date cannot be after end date');
      return;
    }
    if (formData.price <= 0) {
      setError('Price must be greater than 0');
      return;
    }

    if (
      !formData.firstName.trim() ||
      !formData.lastName.trim() ||
      !formData.username.trim() ||
      !formData.email.trim()
    ) {
      setError('Please fill in all user fields');
      return;
    }

    // Role validation: Staff cannot create Admin users
    if (user.role === 'STAFF' && formData.role === 'ADMIN') {
      setError('Staff cannot create Admin users');
      return;
    }

    try {
      setSubmitting(true);

      // Create new user first
      const newUserPayload = {
        firstName: formData.firstName.trim(),
        lastName: formData.lastName.trim(),
        username: formData.username.trim(),
        email: formData.email.trim(),
        role: formData.role,
        password: 'DefaultPassword123!', // Change as needed or implement invite system
      };

      const userResponse = await userAPI.create(newUserPayload);
      const newUser = userResponse.data;

      // Create membership for new user
      const membershipPayload = {
        userId: newUser.id,
        type: formData.type,
        price: parseFloat(formData.price),
        startDate: `${formData.startDate}T00:00:00`,
        endDate: `${formData.endDate}T00:00:00`,
        description: formData.description,
      };

      await membershipAPI.create(membershipPayload);

      setSuccess('User and membership created successfully');
      setShowModal(false);
      resetForm();
      fetchMemberships();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create user or membership');
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (membership) => {
    // Editing membership but NOT user (user management separate)
    if (!canManageUser(membership.user.role)) {
      setError('You do not have permission to edit this membership');
      return;
    }
    setEditingMembership(membership);
    setFormData({
      // Show user info but do not edit here (optional)
      firstName: membership.user.firstName,
      lastName: membership.user.lastName,
      username: membership.user.username,
      email: membership.user.email,
      role: membership.user.role,

      type: membership.type,
      price: membership.price,
      startDate: membership.startDate.split('T')[0],
      endDate: membership.endDate.split('T')[0],
      description: membership.description || '',
    });
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    const membership = memberships.find((m) => m.id === id);
    if (!membership || !canManageUser(membership.user.role)) {
      setError('You do not have permission to delete this membership');
      return;
    }
    if (window.confirm('Are you sure you want to delete this membership?')) {
      try {
        await membershipAPI.delete(id);
        setSuccess('Membership deleted successfully');
        fetchMemberships();
      } catch {
        setError('Failed to delete membership');
      }
    }
  };

  const updateStatus = async (id, newStatus) => {
    const membership = memberships.find((m) => m.id === id);
    if (!membership || !canManageUser(membership.user.role)) {
      setError('You do not have permission to update this membership');
      return;
    }
    try {
      await membershipAPI.updateStatus(id, newStatus);
      setSuccess(`Status updated to ${newStatus}`);
      fetchMemberships();
    } catch {
      setError('Failed to update status');
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      ACTIVE: 'success',
      EXPIRED: 'danger',
      CANCELLED: 'warning',
      PENDING: 'info',
    };
    return <Badge bg={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const getTypeBadge = (type) => {
    const variants = {
      BASIC: 'primary',
      PREMIUM: 'warning',
      VIP: 'danger',
      STUDENT: 'info',
    };
    return <Badge bg={variants[type] || 'secondary'}>{type}</Badge>;
  };

  if (loading) {
    return (
      <div className="text-center mt-4">
        <Spinner animation="border" /> Loading memberships...
      </div>
    );
  }

  return (
    <Container className="mt-4">
      <Card>
        <Card.Header className="d-flex justify-content-between align-items-center">
          <h4>Membership Management</h4>
          <Button
            variant="primary"
            onClick={() => {
              setEditingMembership(null);
              resetForm();
              setShowModal(true);
            }}
          >
            Add New Membership
          </Button>
        </Card.Header>
        <Card.Body>
          {error && (
            <Alert variant="danger" dismissible onClose={() => setError('')}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert variant="success" dismissible onClose={() => setSuccess('')}>
              {success}
            </Alert>
          )}

          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>Member</th>
                <th>Type</th>
                <th>Status</th>
                <th>Price</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {memberships
                .filter((m) => canManageUser(m.user.role))
                .map((m) => (
                  <tr key={m.id}>
                    <td>
                      {m.user.firstName} {m.user.lastName}
                    </td>
                    <td>{getTypeBadge(m.type)}</td>
                    <td>{getStatusBadge(m.status)}</td>
                    <td>${m.price}</td>
                    <td>{new Date(m.startDate).toLocaleDateString()}</td>
                    <td>{new Date(m.endDate).toLocaleDateString()}</td>
                    <td>
                      <Button
                        size="sm"
                        variant="outline-primary"
                        onClick={() => handleEdit(m)}
                      >
                        Edit
                      </Button>{' '}
                      <Button
                        size="sm"
                        variant="outline-danger"
                        onClick={() => handleDelete(m.id)}
                      >
                        Delete
                      </Button>{' '}
                      <Button
                        size="sm"
                        variant="outline-secondary"
                        onClick={() => updateStatus(m.id, 'CANCELLED')}
                      >
                        Cancel
                      </Button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </Table>
        </Card.Body>
      </Card>

      <Modal
        show={showModal}
        onHide={() => {
          setShowModal(false);
          setEditingMembership(null);
          resetForm();
          setError('');
        }}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>
            {editingMembership ? 'Edit Membership' : 'Add New Membership'}
          </Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {!editingMembership && (
              <>
                <h5>User Information</h5>
                <Form.Group className="mb-3">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.firstName}
                    onChange={(e) =>
                      setFormData({ ...formData, firstName: e.target.value })
                    }
                    required
                    disabled={submitting}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.lastName}
                    onChange={(e) =>
                      setFormData({ ...formData, lastName: e.target.value })
                    }
                    required
                    disabled={submitting}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.username}
                    onChange={(e) =>
                      setFormData({ ...formData, username: e.target.value })
                    }
                    required
                    disabled={submitting}
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={formData.email}
                    onChange={(e) =>
                      setFormData({ ...formData, email: e.target.value })
                    }
                    required
                    disabled={submitting}
                  />
                </Form.Group>

                {(user.role === 'ADMIN' || user.role === 'STAFF') && (
                  <Form.Group className="mb-3">
                    <Form.Label>Role</Form.Label>
                    <Form.Select
                      value={formData.role}
                      onChange={(e) =>
                        setFormData({ ...formData, role: e.target.value })
                      }
                      required
                      disabled={submitting}
                    >
                      {user.role === 'ADMIN' && (
                        <>
                          <option value="ADMIN">Admin</option>
                          <option value="STAFF">Staff</option>
                          <option value="TRAINER">Trainer</option>
                          <option value="MEMBER">Member</option>
                        </>
                      )}
                      {user.role === 'STAFF' && (
                        <>
                          <option value="STAFF">Staff</option>
                          <option value="TRAINER">Trainer</option>
                          <option value="MEMBER">Member</option>
                        </>
                      )}
                    </Form.Select>
                  </Form.Group>
                )}

                <hr />
              </>
            )}

            <h5>Membership Information</h5>
            <Form.Group className="mb-3">
              <Form.Label>Type</Form.Label>
              <Form.Select
                value={formData.type}
                onChange={(e) =>
                  setFormData({ ...formData, type: e.target.value })
                }
                required
                disabled={submitting || !!editingMembership}
              >
                <option value="BASIC">BASIC</option>
                <option value="PREMIUM">PREMIUM</option>
                <option value="VIP">VIP</option>
                <option value="STUDENT">STUDENT</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="number"
                step="0.01"
                value={formData.price}
                onChange={(e) =>
                  setFormData({ ...formData, price: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type="date"
                value={formData.startDate}
                onChange={(e) =>
                  setFormData({ ...formData, startDate: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type="date"
                value={formData.endDate}
                onChange={(e) =>
                  setFormData({ ...formData, endDate: e.target.value })
                }
                required
                disabled={submitting}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description (Optional)</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={formData.description}
                onChange={(e) =>
                  setFormData({ ...formData, description: e.target.value })
                }
                disabled={submitting}
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => {
                setShowModal(false);
                setEditingMembership(null);
                resetForm();
                setError('');
              }}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button type="submit" variant="primary" disabled={submitting}>
              {submitting ? (
                <>
                  <Spinner animation="border" size="sm" /> Saving...
                </>
              ) : editingMembership ? (
                'Save Changes'
              ) : (
                'Create Membership'
              )}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </Container>
  );
};

export default MembershipManagement;
