import React, { useEffect, useState } from 'react';
import { Form, Button, Container, Row, Col, Alert, Spinner } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const BookTrainingSession = () => {
  const { user } = useAuth();
  const [trainers, setTrainers] = useState([]);
  const [formData, setFormData] = useState({
    trainerId: '',
    type: 'PERSONAL',
    scheduledDate: '',
    duration: 60,
    price: '',
    notes: '',
    location: '',
  });
  const [loading, setLoading] = useState(true);
  const [feedback, setFeedback] = useState({ type: '', message: '' });

  useEffect(() => {
    const fetchTrainers = async () => {
      try {
        const token = localStorage.getItem('token'); // or however you're storing it
        const response = await axios.get('/api/users/trainers', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setTrainers(response.data);
      } catch (error) {
        setFeedback({ type: 'danger', message: 'Failed to load trainers.' });
      } finally {
        setLoading(false);
      }
    };
  
    fetchTrainers();
  }, []);
  

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFeedback({});

    try {
      const payload = {
        ...formData,
        memberId: user.id,
        scheduledDate: new Date(formData.scheduledDate).toISOString()
      };

      const response = await axios.post('/api/training-sessions', payload);
      setFeedback({ type: 'success', message: 'Training session booked successfully!' });
      setFormData({
        trainerId: '',
        type: 'PERSONAL',
        scheduledDate: '',
        duration: 60,
        price: '',
        notes: '',
        location: '',
      });
    } catch (error) {
      const msg = error.response?.data?.message || 'Failed to book session.';
      setFeedback({ type: 'danger', message: msg });
    }
  };

  return (
    <Container className="mt-4">
      <h2>Book a Training Session</h2>

      {feedback.message && (
        <Alert variant={feedback.type}>{feedback.message}</Alert>
      )}

      {loading ? (
        <div className="text-center my-5">
          <Spinner animation="border" />
        </div>
      ) : (
        <Form onSubmit={handleSubmit}>
          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Trainer</Form.Label>
                <Form.Select
                  name="trainerId"
                  value={formData.trainerId}
                  onChange={handleChange}
                  required
                >
                  <option value="">-- Select Trainer --</option>
                  {trainers.map(trainer => (
                    <option key={trainer.id} value={trainer.id}>
                      {trainer.firstName} {trainer.lastName}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>

            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Session Type</Form.Label>
                <Form.Select
                  name="type"
                  value={formData.type}
                  onChange={handleChange}
                >
                  <option value="PERSONAL">Personal</option>
                  <option value="GROUP">Group</option>
                  <option value="VIRTUAL">Virtual</option>
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Scheduled Date & Time</Form.Label>
                <Form.Control
                  type="datetime-local"
                  name="scheduledDate"
                  value={formData.scheduledDate}
                  onChange={handleChange}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={3}>
              <Form.Group className="mb-3">
                <Form.Label>Duration (minutes)</Form.Label>
                <Form.Control
                  type="number"
                  name="duration"
                  value={formData.duration}
                  onChange={handleChange}
                  min={30}
                  step={15}
                  required
                />
              </Form.Group>
            </Col>

            <Col md={3}>
              <Form.Group className="mb-3">
                <Form.Label>Price ($)</Form.Label>
                <Form.Control
                  type="number"
                  name="price"
                  value={formData.price}
                  onChange={handleChange}
                  min={0}
                  step={1}
                  required
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>Location</Form.Label>
            <Form.Control
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Notes (optional)</Form.Label>
            <Form.Control
              as="textarea"
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows={3}
            />
          </Form.Group>

          <Button type="submit" variant="primary">
            Book Session
          </Button>
        </Form>
      )}
    </Container>
  );
};

export default BookTrainingSession;
